import React from "react";
import { useState } from "react";


const Todo = ({ item, onUpdate, onComplete, onDelete }) => {
  const [isEdit, setIsEdit] = useState(false);
 /*  const [value, setValue] = useState(item.title ?? ""); */

  function FormEdit() {
    const [newValue, setNewValue] = useState(item.title);
    setNewValue(value);

    function handleSubmit(e) {
      onUpdate(item.id, value);
      setIsEdit(false);
    }

    function handleChange(e) {
      const value = e.target.value;
      setNewValue(value);
    }

    function handleClickUpdate() {
      onUpdate(item.id, value);
      setIsEdit(false);
    }

    function handleCheckboxChange(e) {
      onComplete(item.id, e.target.checked);
    }

    return (
      <div className="todo">
      {isEdit ? (
        <form className="todoUpdateForm" onSubmit={handleSubmit}>
          <input
          type="text"
          className="todoInput"
          onChange={handleChange}
          value={value}
        />
        <button className="button" onClick={handleClickUpdate}>
          Update
        </button>
      </form>
      ) : (
        <div className="todoInfo">
          <input
            type={"checkbox"}
            onChange={handleCheckboxChange}
            checked={item.checked}
          />
          <span
            className="todoTitle"
            style={{
              color: item.completed ? "#ccc" : "",
              textDecoration: item.completed ? "line-through" : "",
            }}
          >
            {item.title}
          </span>
          <button className="button" onClick={() => setIsEdit(true)}>
            Edit
          </button>
          <button className="buttonDelete" onClick={() => onDelete(item.id)}>
            Update
          </button>
        </div>
      )}
    </div>
  );
}

  function TodoElement() {
    return (
      <div className="todoInfo">
        {item.title} <button onClick={() => setIsEdit(true)}>Edit</button>
        <button>Delete</button>
      </div>
    );
  }
  return <div className="todo">{isEdit ? <FormEdit /> : <TodoElement />}</div>;
};

export default Todo;
